const ItemButtonIcon = styled.div`
  width: 20px;
  height: 20px;
  margin-right: 7px;
  background: 0 0 no-repeat;
  &.Metamask {
    background-image: url(data:image/svg+xml;base64,);
  }

  &.Fortmatic {
    background-image: url(data:image/svg+xml;base64,);
  }

  &.Torus {
    background-image: url(data:image/svg+xml;base64,);
  }

  &.Portis {
    background-image: url(data:image/svg+xml;base64,);
  }

  &.WalletConnect {
    background-position: 0;
    background-image: url(data:image/svg+xml;base64,);
  }
`

const ItemButtonName = styled.div`
  font-size: 12px;
  font-weight: 500;
  position: relative;
  top: 1px;
`

const UnconnectorButton = styled.button`
  margin-right: auto;
  width: 80%;
  height: 40px;
  margin: 24px 0 0 0;
  padding: 0;
  border: none;
  border-radius: 20px;
  background-color: ${({ theme }) => theme.colors.blueGray50};
  color: ${({ theme }) => theme.colors.textColor};
  font-size: 13px;
  font-weight: 500;
  font-family: ${({ theme }) => theme.fontFamilies.notoSans};
  letter-spacing: 1px;
  justify-content: center;
  align-items: center;
  cursor: pointer;
`

export default function ImportAccount(props) {
  const { videoChild, token } = props

  const bond = {
    eth: '0x77f973fcaf871459aa58cd81881ce453759281bc',
    usdc: '0xF013406A0B1d544238083DF0B93ad0d2cBE0f65f',
    dai: '0x493C57C4763932315A328269E1ADaD09653B9081',
  }

  const tToken = {
    eth: '0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2',
    usdc: '0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48',
  }

  const decimal = {
    eth: 1e18,
    dai: 1e18,
    usdc: 1e6,
  }
  const { t } = useTranslation()

  const {
    account,
    active,
    activate,
    deactivate,
    connector,
    library,
  } = useWeb3React()

  const isDetectedWeb3 = !!window.ethereum || !!window.web3

  const [balance, setbalance] = useState(0)
  const [ibalance, setIBalance] = useState(0)

  // get user iToken balance
  useMemo(() => {
    ;(async () => {
      try {
        if (active && connector) {
          const iToken = getContract(bond[token], IErc20_ABI, library, account)
          const amount = new BigNumber(
            await iToken.methods.balanceOf(account).call(),
          )

          setbalance(amount.div(decimal[token]).toString())
        } else {
          setbalance(0)
        }
      } catch (e) {
        console.log(e)
      }
    })()
  }, [account, active, connector, library])

  // get eth amount in fulcrum pool
  useMemo(() => {
    ;(async () => {
      const a = async () => {
        try {
          if (active && connector) {
            if (token == 'dai') {
              const iToken = getContract(
                bond[token],
                IErc20_ABI,
                library,
                account,
              )
              const totalAssetSupply = new BigNumber(
                await iToken.methods.totalAssetSupply().call(),
              )
              const totalAssetBorrow = new BigNumber(
                await iToken.methods.totalAssetBorrow().call(),
              )
              const amount = totalAssetSupply.minus(totalAssetBorrow)
              setIBalance(
                amount.gt(0) ? amount.div(decimal[token]).toString() : 0,
              )
            } else {
              const wEth = getContract(
                tToken[token],
                Erc20_ABI,
                library,
                account,
              )
              const amount = new BigNumber(
                await wEth.methods.balanceOf(bond[token]).call(),
              )

              setIBalance(
                amount.gt(0) ? amount.div(decimal[token]).toString() : 0,
              )
            }
          } else {
            setIBalance(0)
          }
        } catch (e) {
          console.log(e)
        }
      }
      await a()
      setInterval(async () => {
        await a()
      }, 120000)
    })()
  }, [account, active, connector, library])

  const isInjected = useMemo(() => active && connector === injectedConnector, [
    active,
    connector,
  ])

  const isWalletConnected = useMemo(
    () => active && connector === walletconnectConnector,
    [active, connector],
  )

  const isFortmaticConnected = useMemo(
    () => active && connector === fortmaticConnector,
    [active, connector],
  )

  const isTorusConnected = useMemo(
    () => active && connector === torusConnector,
    [active, connector],
  )

  const isPortisConnected = useMemo(
    () => active && connector === portisConnector,
    [active, connector],
  )

  const connectInjectedWallet = useCallback(async () => {
    try {
      await activate(injectedConnector, undefined, true)
    } catch (e) {
      console.log(e)
    }
  }, [activate])

  const connectFortmatic = useCallback(async () => {
    try {
      await activate(fortmaticConnector, undefined, true)
    } catch (e) {
      console.log(e)
    }
  }, [activate])

  const connectTorus = useCallback(async () => {
    try {
      await activate(torusConnector, undefined, true)
    } catch (e) {
      console.log(e)
      await torusConnector.deactivate()
    }
  }, [activate])

  const connectPortis = useCallback(async () => {
    try {
      await activate(portisConnector, undefined, true)
    } catch (e) {
      console.log(e)
    }
  }, [activate])

  const connectWalletconnect = useCallback(async () => {
    try {
      await activate(walletconnectConnector, undefined, true)
    } catch (e) {
      console.log(e)
    }
  }, [activate])

  const unconnectWallet = useCallback(() => {
    deactivate()
    if (connector === walletconnectConnector) {
      connector.close()
    }
  }, [connector, deactivate])

  const particialAddress = account => {
    const prefixAddress = account.slice(0, 8)
    const suffixAddress = account.slice(account.length - 8, account.length)
    return `${prefixAddress}...${suffixAddress}`
  }

  return (
    <ImportAccountContainer>
      {(isInjected ||
        isWalletConnected ||
        isFortmaticConnected ||
        isTorusConnected ||
        isPortisConnected) && (
        <>
          <ImportAccountBox>
            <ValueBox>
              <ImportAccountTitleContainer>
                <ImportAccountTitle>Account :</ImportAccountTitle>
                <ImportAccountValue>
                  {particialAddress(account)}
                </ImportAccountValue>
              </ImportAccountTitleContainer>
              <ImportAccountTitleContainer>
                <ImportAccountTitle>
                  Your {token.toUpperCase()}:
                </ImportAccountTitle>
                <ImportAccountValue>{balance}</ImportAccountValue>
              </ImportAccountTitleContainer>
              <ImportAccountTitleContainer>
                <ImportAccountTitle>Contract:</ImportAccountTitle>
                <ImportAccountValue>{ibalance}</ImportAccountValue>
              </ImportAccountTitleContainer>
              <UnconnectorButton onClick={unconnectWallet}>
                {t('logout')}
              </UnconnectorButton>
            </ValueBox>
            {videoChild}
          </ImportAccountBox>
        </>
      )}
      {!(
        isInjected ||
        isWalletConnected ||
        isFortmaticConnected ||
        isTorusConnected ||
        isPortisConnected
      ) && (
        <>
          <ImportAccountContent>
            <ImportAccountItem className='import-account__item'>
              <ImportAccountButton
                isConnected={isInjected}
                disabled={!isDetectedWeb3}
                onClick={connectInjectedWallet}
              >
                <ItemButtonIcon className='ItemButtonIcon Metamask'></ItemButtonIcon>
                <ItemButtonName className='ItemButtonName Metamask'>
                  METAMASK
                </ItemButtonName>
              </ImportAccountButton>
            </ImportAccountItem>
            <ImportAccountItem className='import-account__item'>
              <ImportAccountButton
                isConnected={isFortmaticConnected}
                onClick={connectFortmatic}
              >
                <ItemButtonIcon className='ItemButtonIcon Fortmatic'></ItemButtonIcon>
                <ItemButtonName className='ItemButtonName Fortmatic'>
                  FORTMATIC
                </ItemButtonName>
              </ImportAccountButton>
            </ImportAccountItem>
            <ImportAccountItem className='import-account__item'>
              <ImportAccountButton
                isConnected={isTorusConnected}
                onClick={connectTorus}
              >
                <ItemButtonIcon className='ItemButtonIcon Torus'></ItemButtonIcon>
                <ItemButtonName className='ItemButtonName Torus'>
                  TORUS
                </ItemButtonName>
              </ImportAccountButton>
            </ImportAccountItem>
            <ImportAccountItem className='import-account__item'>
              <ImportAccountButton
                isConnected={isPortisConnected}
                onClick={connectPortis}
              >
                <ItemButtonIcon className='ItemButtonIcon Portis'></ItemButtonIcon>
                <ItemButtonName className='ItemButtonName Portis'>
                  PORTIS
                </ItemButtonName>
              </ImportAccountButton>
            </ImportAccountItem>
            <ImportAccountItem className='large import-account__item'>
              <ImportAccountButton
                isConnected={isWalletConnected}
                onClick={connectWalletconnect}
              >
                <ItemButtonIcon className='ItemButtonIcon WalletConnect'></ItemButtonIcon>
                <ItemButtonName className='ItemButtonName WalletConnect'>
                  WALLET CONNECT
                </ItemButtonName>
              </ImportAccountButton>
            </ImportAccountItem>
          </ImportAccountContent>
        </>
      )}
    </ImportAccountContainer>
  )
}
